import React from 'react';
export default function Task ({ todo, toggleTodo}) {
    function handleTodoClick(){
        toggleTodo(todo.id)
    }
    return (
      <div>
        <label> 
            <input type= "checkbox" checked={todo.complete} onChange={handleTodoClick} /> 
          {todo.name}
          </label>
      </div>
    )
  }
