import React, { useState, useRef, useEffect } from 'react';
import Task from './Task.js';
import TaskList from './TaskList.js';
import uuidv4 from 'uuid/v4';

const LOCAL_STORAGE_KEY = 'todoApp.todos'

function Main () {

  const [todos, setTodos] = useState([{ id: 1, name:'Todo 1',complete: false }])
  const todoNameRef =useRef()

  //to save our todos on the page 
  useEffect(() => {
      const storedTodos= JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY))
      if(storedTodos) { 
        setTodos(storedTodos);
      }
  }, [])

//any time the array changes, useEffect is called 
  useEffect(() => {
     localStorage.setItem(LOCAL_STORAGE_KEY,JSON.stringify(todos))
  }, [todos])




  function handleAddTodo (e){
   const name= todoNameRef.current.value;
   if(name === " ") return 
   setTodos(prevTodos => {
      return [...prevTodos,{id: uuidv4(), name: name , complete: false }]
   })

   todoNameRef.current.value = null 



  }

  function toggleTodo(id){
    const newTodos =[...todos]; 
    const todo = newTodos.find(todo=>todo.id ===id)
    todo.complete= !todo.complete
    setTodos(newTodos)

  }

  return (
          <div>  
            <TaskList todos={todos} toggleTodo={toggleTodo}/>   
            <input ref={todoNameRef} type = "text"></input>
            <button  onClick={handleAddTodo} id= "add">Add Todo </button> 
            <button id= "delete">  Delete Todo </button>
            <div id="left"> 0 left to do </div>
          </div>
        );

}


export default Main;
